import '../css/footer.css';

function Footer({ items }) {
  return (
    <div className="footerWrapper">
      <div>Total Item: <span style={{ color: 'blue', fontWeight: 'bold' }}>{items.length}</span></div>
      <br />
      <div>Completed Item: <span style={{ color: 'green', fontWeight: 'bold' }}>{items.filter(item => item.packed).length}</span></div>
      <br />
      <div>Remaining Item: <span style={{ color: 'red', fontWeight: 'bold' }}>{items.length - items.filter(item => item.packed).length}</span></div>
    </div>
  );
}

export default Footer;
